import { faToggleOff, faToggleOn } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import React, { useState } from "react";
import '../styles/Description.scss';

export default function Description() {
  const [isLangToggle, setIsLangToggle] = useState(true);
  const iconStyle = {marginRight: 10,fontSize: 35};
  var toggleBtn;

  if (isLangToggle) {
    toggleBtn=(
      <FontAwesomeIcon icon={faToggleOff} style={iconStyle}/>
    );
  } else {
    toggleBtn=(
      <FontAwesomeIcon icon={faToggleOn} style={iconStyle}/>
    )
  }

  return(
    <div className="descript">
      <p id="pc">
        デジタルデザイナー兼デベロッパーを目指し日々勉強中です。<br/>
        プログラミングを通し、ユーザが使いやすいUI/UXデザイン、扱いやすいシステムを作りたいと考えプログラミング活動を行っています。<br />
        現在できること、ホームページ制作、ロゴ作成
        幼少期にドイツのミュンヘンに２年間住んでおり、ヨーロッパ諸国の街や人、物に関り海外のデザインや考え方に触れてきて感受性豊かで、<br />
        高校、大学とダンスをしてきて表現について学び、社会に出てからは、服飾に興味を持ちアパレルで働きながら独学で勉強その後洋裁学校に通いテーラーに弟子入りし、洋服の作り方やデザインについて学びどのような服が着やすくて、使いやすいかを考え作っていました。<br />
        またその時から、洋服作成をデジタル化したらもっとよりよい服作りができるようになるのではないかと考え、プログラミングの勉強を開始し、現在に至ります。<br />
        現在では、プログラミングを通しユーザが使いやすいデザイン、使いやすいシステムを作りたいと考えプログラミング活動を行っています。
      </p>
      <p id="pc"></p>
      <div id="phone">
      <div
        className="lang-tog"
        onClick={() => {
          setIsLangToggle((prev) => !prev);
        }}
      >
        {toggleBtn}English
        </div>
          <p id={ isLangToggle ? "display" : "none"}>日本語</p>
          <p id={ isLangToggle ? "none" : "display"}>english</p>
      </div>
    </div>
  );
}
