import React, { useEffect, useState } from "react";
import { useSpring, animated } from 'react-spring';
import { useNavigate } from "react-router-dom";
import '../styles/Main.scss';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowUp } from '@fortawesome/free-solid-svg-icons';
import SkillsPart from "../components/SkillsPart";
import { skillsList } from '../contents/skillsList';

export default function Main() {
  const iconStyle = {padding: 10, fontSize: 40, color: 'white'};
  const [ trigger, setTrigger ] = useState(false);
  const [scrollY, setScrollY ] = useState(0);
  const navi = useNavigate();
  var topBtn;

  const fadeStyle = useSpring({ opacity: trigger ? 1 : 0 });
  const handleScroll = () => {setScrollY(window.pageYOffset)};
  const handleTrigger = () => {setTrigger(_trigger => !_trigger)};

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const toTop = () => {
    window.scrollTo({
       top: 0,
       behavior: "smooth",
    });
  };

  if (scrollY >= 500) {
    topBtn = (
      <div className="top-scroll" 
        onClick={() => {toTop()}} 
        style={{
          opacity: '1',
        }}
      >
      <FontAwesomeIcon style={iconStyle} icon={faArrowUp}/>
      </div>
    );
  } else {
    topBtn = (
      <div className="top-scroll" 
        onClick={() => {toTop()}} 
        style={{
          opacity: '0',
        }}
      >
      <FontAwesomeIcon style={iconStyle} icon={faArrowUp}/>
      </div>
    );
  }

  return(
    <div className="main">
      <div className="main-visual">
        <h2 className="main-letter">Digital Designer</h2>
      </div>
      <animated.div className="about" style={fadeStyle}>
        <div className="portrait"></div>
        <div className="about-content">
        </div>
        <div className="about-btn" onClick={() => {navi('/experience')}}>詳しく見る</div>
      </animated.div>
      <div className="skills">
        <div className="skill-list">
        {skillsList.map((skill) => {
          return(
            <SkillsPart name={skill.name} iconName={skill.iconName}/>
          );
        })}
        </div>
      </div>
      <div className="contact-banner">
        <h3>Contact</h3>
        <div className="banner">
          <div className="contact-btn" onClick={() => {navi('/contact')}}>Click Me</div>
        </div>
      </div>
      {topBtn}
    </div>
  );
}
