import React, { useEffect, useState } from "react";
import { useInView } from "react-intersection-observer";
import { useNavigate } from "react-router-dom";
import '../styles/Main.scss';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowUp } from "@fortawesome/free-solid-svg-icons";
import SkillsPart from "../components/SkillsPart";
import { skillsList } from '../contents/skillsList';

export default function Main() {
  const iconStyle = {padding: 10, fontSize: 40, color: 'white'};
  const [scrollY, setScrollY ] = useState(0);
  const { ref, inView } = useInView({
    threshold: 0,
  });
  const navi = useNavigate();
  var topBtn;

  const handleScroll = () => {setScrollY(window.pageYOffset)};

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const toTop = () => {
    window.scrollTo({
       top: 0,
       behavior: "smooth",
    });
  };

  if (scrollY >= 500) {
    topBtn = (
      <div className="top-scroll" 
        onClick={() => {toTop()}} 
        style={{
          opacity: '1',
        }}
      >
      <FontAwesomeIcon style={iconStyle} icon={faArrowUp}/>
      </div>
    );
  } else {
    topBtn = (
      <div className="top-scroll" 
        onClick={() => {toTop()}} 
        style={{
          opacity: '0',
        }}
      >
      <FontAwesomeIcon style={iconStyle} icon={faArrowUp}/>
      </div>
    );
  }

  return(
    <div className="main">
      <div className="main-visual">
        <h2 className="main-letter">Digital Designer</h2>
      </div>
      <div className="about">
        <div className="portrait"></div>
        <div className="about-content">
        </div>
        <div className="about-btn" onClick={() => {navi('/experience')}}>To Experience</div>
      </div>
      <div className="skills">
        {skillsList.map((skill) => {
          return(
            <SkillsPart />
          );
        })}
      </div>
      <div className="contact-banner"></div>
      {topBtn}
    </div>
  );
}
